import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'firstAngular';
  isUnchanged = false;
  slogan:string='this is my first angular project';
  displaySlogan:boolean=true;
  displayMethod(){
    if(this.displaySlogan===true){
      this.displaySlogan=false;
    }else{
      this.displaySlogan=true;
    }

  }
  firstName: string='Enter your First Name';
  lastName: string='Enter your Last Name';
  contactNumber:string='Enter your Number';
  dateOfBirth:string='dd/mm/yyyy';
}
<div>

  <app-header></app-header>

  <!-- <app-main></app-main> -->
  <!-- <div>
    <h2 [hidden]="displaySlogan">{{slogan}}</h2>
  </div> -->
  <!-- <button type="button" [disabled]="isUnchanged" (click)="displayMethod()">Display Button</button> -->
 <div class="detail">
  <div>
    <span><b>First Name</b></span>
    <input type="text" [(ngModel)]="firstName">
  </div>

   <span><b>Last Name</b></span>
   <input type="text" [(ngModel)]="lastName">
   <span><b>Contact Number</b></span>
   <input type="text" [(ngModel)]="contactNumber">
   <span><b>Date of Birth</b></span>
   <input type="text" [(ngModel)]="dateOfBirth">
  </div>

</div>
