import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'firstAngular';
  isUnchanged = false;
  slogan:string='this is my first angular project';
  displaySlogan:boolean=true;
}
